  labs(title = "Vuelta más rápida en cada GP de Italia", 
       x = "Año", y = "Tiempo (segundos)",
       caption = "FIA 2025") +
  theme_solarized() + 
  scale_y_continuous(breaks = seq(70, 120, by = 5)) +
  scale_x_continuous(breaks = seq(1950, 2022, by= 3)) +
